from graphe import Graphe
from union_find import UnionFind
import random

class Labyrinthe:
    def __init__(self,largeur,hauteur,bouclitude=None):
        self.graphe = Graphe()
        self.hauteur=hauteur
        self.largeur=largeur
        for i in range(hauteur):
            for j in range(largeur):
                self.graphe.ajouter_noeud((i,j))
        # Pour la question 5.
        uf = UnionFind()
        for i in range(hauteur):
            for j in range(largeur):
                uf.find((i,j))
        es=[]
        for i in range(hauteur):
            for j in range(largeur):
                if i+1<hauteur:
                    es.append([(i,j),(i+1,j)])
                if j+1<largeur:
                    es.append([(i,j),(i,j+1)])
        random.shuffle(es)
        for e in es:
            u,v=e[0],e[1]
            if uf.find(u)!=uf.find(v) or bouclitude and random.random()<bouclitude:
                p=random.random()
                if p<.2:
                    w=2
                elif p>0.93:
                    w=5
                else:
                    w=1
                self.graphe.ajouter_arete(u,v,w)
                uf.union(u,v)
                
        # Questions 4 et 5 :
        # modifier le code ci-dessous
        # self.graphe.ajouter_noeud((0,0))

    def __str__(self):
    # Génère une chaine de caractères à partir de
    # l'objet : si laby est un objet labyrinthe
    # str(laby) fait en réalité laby.__str__()
    # et implicitement print(laby) affiche cette
    # chaîne
        c={1:' ',2:'.',5:'@'}
        s='#'*(2*self.largeur+1)+'\n'
        for i in range(self.hauteur):
            ligne='#'
            ligne2='#'
            for j in range(self.largeur):
                if j!=self.largeur-1 and (i,j+1) in self.graphe.voisins((i,j)):
                    w=self.graphe.poids((i,j),(i,j+1))
                    ligne+=' '+c[w]
                else:
                    ligne+=' #'
                if i!=self.hauteur-1 and (i+1,j) in self.graphe.voisins((i,j)):
                    w=self.graphe.poids((i,j),(i+1,j))
                    ligne2+=c[w]+'#'
                else:
                    ligne2+='##'
            s+=ligne+'\n'+ligne2+'\n'
        return s

    def solution(self):
        return self.graphe.pcc((0,0),(self.hauteur-1,self.largeur-1))[0]

    def str_pcc(self):
        chemin=self.graphe.pcc((0,0),(self.hauteur-1,self.largeur-1))[0]
        rojo=[]
        orig=chemin[0]
        rojo.append((1,1))
        for dest in chemin[1:]:
            rojo.append( (orig[0]+dest[0]+1,orig[1]+dest[1]+1) )
            rojo.append((dest[0]*2+1,dest[1]*2+1))
            #print('---->',orig,dest,rojo)
            orig=dest
        c={1:' ',2:'.',5:'@'}
        s='#'*(2*self.largeur+1)+'\n'
        for i in range(self.hauteur):
            ligne='#'
            ligne2='#'
            for j in range(self.largeur):
                if (2*i+1,2*j+1) in rojo:
                    ligne+='/'
                else:
                    ligne+=' '
                if j!=self.largeur-1 and (i,j+1) in self.graphe.voisins((i,j)):
                    w=self.graphe.poids((i,j),(i,j+1))
                    if (2*i+1,2*j+2) in rojo:
                        ligne+='/'
                    else:
                        ligne+=c[w]
                else:
                    ligne+='#'
                if i!=self.hauteur-1 and (i+1,j) in self.graphe.voisins((i,j)):
                    w=self.graphe.poids((i,j),(i+1,j))
                    if (2*i+2,2*j+1) in rojo:
                        ligne2+='/#'
                    else:
                        ligne2+=c[w]+'#'
                else:
                    ligne2+='##'
            s+=ligne+'\n'+ligne2+'\n'
        return s
        
            
        
            
